/// <reference types="node" resolution-mode="require"/>
declare type DeployData = {
    file: Buffer;
    filePath: string;
    fileName: string;
};
declare type Credentials = {
    apiKey: string;
    apiSecret: string;
};
declare type ApiClient = {
    deploy: (data: DeployData) => Promise<void>;
    validate: () => Promise<boolean>;
};
declare const ApiClient: ({ apiKey, apiSecret }: Credentials) => ApiClient;
export default ApiClient;
