import path from 'path';

import getProjectPwd from './get-project-pwd.js';

const DEFAULT_CONFIG_FILE = 'adminjs-cloud.json';

/**
 * Get the configuration file path in current run folder
 */
const getConfigurationFilePath = (
  configPath: string = DEFAULT_CONFIG_FILE,
): string => path.join(getProjectPwd(), configPath);

export default getConfigurationFilePath;
